mapper.select:
SELECT `type`, `guige`, `danwei`, SUM(`count`) AS total_count FROM `t_inventory` WHERE
`deleted_flag` = 0
and type = #{queryForm.type}
and guige = #{queryForm.guige}
and status = #{queryForm.status}
GROUP BY `type`, `guige`, `danwei`
ORDER BY `type`, `guige`, `danwei`
LIMIT #{limit} OFFSET #{offset};

SELECT count(*) FROM (
SELECT `type`, `guige`, `danwei`, SUM(`count`) AS total_count FROM `t_inventory` WHERE
`deleted_flag` = 0
and type = #{queryForm.type}
and guige = #{queryForm.guige}
and status = #{queryForm.status}
GROUP BY `type`, `guige`, `danwei` ) AS summary;

SELECT
*
FROM t_inventory



        and guige = #{queryForm.guige}



        and type = #{queryForm.type}



        and status = #{queryForm.status}



        AND DATE_FORMAT(create_time, '%Y-%m-%d') >= #{queryForm.createTimeBegin}



        AND DATE_FORMAT(create_time, '%Y-%m-%d') <= #{queryForm.createTimeEnd}




ORDER BY id DESC
LIMIT #{limit} OFFSET #{offset}

SELECT count(*)
FROM t_inventory



        and guige = #{queryForm.guige}



        and type = #{queryForm.type}



        and status = #{queryForm.status}



        AND DATE_FORMAT(create_time, '%Y-%m-%d') >= #{queryForm.createTimeBegin}



        AND DATE_FORMAT(create_time, '%Y-%m-%d') <= #{queryForm.createTimeEnd}

mapper.update:
update t_inventory set deleted_flag = #{deletedFlag}
where id in

    #{item}

update t_inventory set status = #{status}
where id in

    #{item}
