mapper.select:
SELECT
*
FROM t_inventory



        AND t_inventory.guige = #{queryForm.guige}



        AND DATE_FORMAT(t_inventory.create_time, '%Y-%m-%d') >= #{queryForm.createTimeBegin}


        AND DATE_FORMAT(t_inventory.create_time, '%Y-%m-%d') <= #{queryForm.createTimeEnd}

